mapper.update:
WITH UPSERT AS (
UPDATE
kcs.tb_sample
SET user_name = #{userName}
, user_phone = #{userPhone}
WHERE user_id = #{userId} RETURNING *
)
INSERT
INTO kcs.tb_sample (
user_id
, user_name
, user_phone)
SELECT #{userId}
, #{userName}
,
#{userPhone} WHERE NOT EXISTS ( SELECT * FROM UPSERT )

mapper.insert:
INSERT INTO
kcs.tb_sample (
user_id
, user_name
, user_phone
) VALUES (
#{userId}
,
#{userName}
, #{userPhone}
)

mapper.update:
UPDATE kcs.tb_sample SET
user_name = #{userName}
, user_phone = #{userPhone}
WHERE user_id =
#{userId}
